+                    Object[] subArray = (Object[]) element;
+                    return element.getClass().getComponentType().getName() + "[" + subArray.length + "]" + "[" + length + "]";
+                }
+            }
+        }
+        return array.getClass().getComponentType().getName() + "[" + length + "]";
+    }
