+package org.openjdk.bench.vm.compiler;
+
+import org.openjdk.jmh.annotations.*;
+
+import java.util.Objects;
+import java.util.concurrent.TimeUnit;
+
+@Warmup(iterations = 3, time = 1)
+@Measurement(iterations = 5, time = 1)
+@BenchmarkMode(Mode.Throughput)
+@OutputTimeUnit(TimeUnit.SECONDS)
+@State(Scope.Thread)
+@Fork(value=3)
+public class CountedLoopCastIV {
+    @Param({"1024", "1536", "2048"})
+    private int limit;
+
+    private static final int LEN = 2048;
+    private int start;
+    private int[] arr;
+
+    @Setup
+    public void init() {
+        arr = new int[LEN];
+        for (int i = 0; i < LEN; i++) {
+            arr[i] = i;
+        }
+
+        start = 0;
+        limit = Math.min(limit, LEN - 4);
+    }
+
+    @Benchmark
+    public void loop_iv_int() {
+        int i = start;
+        while (i < limit) {
+            Objects.checkIndex(i, LEN - 1);
+            int a = arr[i + 1];
+            Objects.checkIndex(i, LEN - 3);
+            arr[i + 3] = a;
+            i++;
+        }
+    }
+
+    @Benchmark
+    public void loop_iv_long() {
+        for (long i = start; i < limit; i++) {
+            Objects.checkIndex(i, LEN - 1);
+            int a = arr[(int)i + 1];
+            Objects.checkIndex(i, LEN - 3);
+            arr[(int)i + 3] = a;
+        }
+    }
+}

