+        try {
+            if (sampleSize <= LOW_RATE_UPPER_BOUND) {
+                samplePointsPerWindow = sampleSize;
+                windowDurationMillis = periodMillis;
+                return;
+            }
+            if (periodMillis == MINUTE && sampleSize < TEN_PER_1000_MS_IN_MINUTES) {
+                samplePointsPerWindow = sampleSize;
+                windowDurationMillis = periodMillis;
+                return;
