+        StringJoiner sb = new StringJoiner(", ");
+        sb.add("measuredPopulationSize=" + measuredPopulationSize);
+        sb.add("samplingInterval=" + samplingInterval);
+        sb.add("projectedPopulationSize=" + projectedPopulationSize);
+        return sb.toString();
+    }
