@@ -258,0 +258,12 @@
+  // -XX:UseLibmIntrinsic or -XX:-InlineIntrinsics disables all intrinsics
+  // listed in the following switch statement
+  if (!UseLibmIntrinsic || !InlineIntrinsics) {
+    switch (id) {
+    case vmIntrinsics::_dtanh:
+    case vmIntrinsics::_dcbrt:
+      return true;
+    default:
+      break;
+    }
+  }
+
