+  } else {
+    Assembler::push(src);
+  }
+}
+
+void MacroAssembler::paired_pop(Register dst) {
+  if (VM_Version::supports_apx_f()) {
+    popp(dst);
+  } else {
+    Assembler::pop(dst);
+  }
+}
