+    @Test
+    public void testModuleResources() throws IOException {
+        try (ImageReader reader = ImageReader.open(image)) {
+            assertNode(reader, "/modules/modfoo/com/foo/Alpha.class");
+            assertNode(reader, "/modules/modbar/com/bar/One.class");
