@@ -716,1 +716,1 @@
-// Helper, on 32bit, for os::has_allocatable_memory_limit
+// Helper, on 32bit, for os::allocatable_memory_limit
@@ -735,12 +735,6 @@
-bool os::has_allocatable_memory_limit(size_t* limit) {
-  struct rlimit rlim;
-  int getrlimit_res = getrlimit(RLIMIT_AS, &rlim);
-  // if there was an error when calling getrlimit, assume that there is no limitation
-  // on virtual memory.
-  bool result;
-  if ((getrlimit_res != 0) || (rlim.rlim_cur == RLIM_INFINITY)) {
-    result = false;
-  } else {
-    *limit = (size_t)rlim.rlim_cur;
-    result = true;
-  }
+bool os::allocatable_memory_limit(size_t* limit) {
+  // On POSIX systems, the amount of allocatable memory is limited by the
+  // size of the virtual address space.
+  *limit = address_space_limit();
+  bool is_limited = *limit != SIZE_MAX;
+
@@ -748,1 +742,1 @@
-  return result;
+  return is_limited;
@@ -799,0 +793,18 @@
+size_t os::address_space_limit() {
+  struct rlimit rlim;
+  int getrlimit_res = getrlimit(RLIMIT_AS, &rlim);
+
+  // If there was an error calling getrlimit, conservatively assume no limit.
+  if (getrlimit_res != 0) {
+    return SIZE_MAX;
+  }
+
+  // If the current limit is not infinity, there is a limit.
+  if (rlim.rlim_cur != RLIM_INFINITY) {
+    return (size_t)rlim.rlim_cur;
+  }
+
+  // No limit
+  return SIZE_MAX;
+}
+

@@ -900,1 +900,1 @@
-bool os::has_allocatable_memory_limit(size_t* limit) {
+bool os::allocatable_memory_limit(size_t* limit) {
@@ -908,0 +908,5 @@
+size_t os::address_space_limit() {
+  // Virtual address space cannot be limited on Windows.
+  return SIZE_MAX;
+}
+

@@ -33,11 +33,0 @@
