+    // 8313657 socket is not closed until GC is run
+    private void closeOpenedResource() {
+        try {
+            if (conn != null) {
+                if (conn.outStream != null) {
+                    conn.outStream.close();
+                }
+
+                if (conn.sock != null && !conn.sock.isClosed()) {
+                    conn.sock.close();
+                }
+            }
+        } catch (IOException ioEx) {
+            //ignore the error;
+        }
+    }
