+        int count = this.count;
+        byte[] value = ensureCapacitySameCoder(this.value, coder, count + 2);
+        if (isLatin1(coder)) {
+            value[count    ] = (byte)c1;
+            value[count + 1] = (byte)c2;
+        } else {
+            StringUTF16.putChar(value, count, c1);
+            StringUTF16.putChar(value, count + 1, c2);
+        }
+        this.count = count + 2;
+    }
