+#ifndef PRODUCT
+  if (_trace.is_trace_overlap()) {
+    tty->print("Always Overlap: %s, distance_lo: %d, distance_hi: %d, aliasing: ",
+               is_always_overlap ? "true" : "false", distance_lo, distance_hi);
+    aliasing.print_on(tty);
+    tty->cr();
+  }
+#endif
+
+  return is_always_overlap;
