+    // exception of wait() timeouts and interrupts the monitor owner
@@ -2242,1 +2242,3 @@
-  assert(was_notified || state == ObjectWaiter::TS_RUN, "");
+  assert(was_notified || state == ObjectWaiter::TS_RUN,
