+            if (qr[1].signum != 0) { // non-0 remainder
+                break;
+            } else {
+                b = qr[0];
+                p5 += 1 << i;
+            }
+        }
+        i--;
+
+        // Factor out all remaining powers of 5 from b
+        int log5b = log5Upper(b);
+        if (log5b < 1 << i)
