+                continue;
+            }
+            int[] folded = CaseFolding.foldIfDefined(cp1);
+            if (folded != null) {
+                if (folded.length > 1) {
+                    return compareToFC0_UTF16(value, k, last, other, k, olast);
+                }
+                cp1 = folded[0];
+            }
+            folded = CaseFolding.foldIfDefined(cp2);
+            if (folded != null) {
+                if (folded.length > 1) {
+                    return compareToFC0_UTF16(value, k, last, other, k, olast);
+                }
+                cp2 = folded[0];
+            }
+            if (cp1 != cp2) {
+                return cp1 - cp2;
+            }
+        }
+        return last - olast;
+    }
