+    public static int scenarioCount(String stdErr) {
+        Pattern pattern = Pattern.compile("Scenario flags");
+        Matcher matcher = pattern.matcher(stdErr);
+        int count = 0;
+        while (matcher.find()) {
+            count++;
+        }
+        return count;
+    }
