+    static class TestCase {
+        private Set<Set<String>> inputFlags;
+        private Set<Set<String>> expectedScenariosWithFlags;
+
+        public TestCase inputFlags(Set<Set<String>> inputFlags) {
+            this.inputFlags = inputFlags;
