+template<typename T, typename U = T>
+class AsanPoisoningHelper {
+  U* _memory_region;
+ public:
+  AsanPoisoningHelper() = delete;
+  AsanPoisoningHelper(U* addr) : _memory_region(addr) {
+    #if INCLUDE_ASAN
+      ASAN_UNPOISON_MEMORY_REGION(_memory_region, sizeof(T));
+    #endif
+  }
+  ~AsanPoisoningHelper() {
+    #if INCLUDE_ASAN
+      ASAN_POISON_MEMORY_REGION(_memory_region, sizeof(T));
+    #endif
+  }
+  static void poison_memory(U* addr) {
+    #if INCLUDE_ASAN
+      ASAN_POISON_MEMORY_REGION(addr, sizeof(T));
+    #endif
+  }
+  static void unpoison_memory(U* addr) {
+    #if INCLUDE_ASAN
+      ASAN_UNPOISON_MEMORY_REGION(addr, sizeof(T));
+    #endif
+  }
+};
+
+// In non-ASAN builds we use 'void' as template parameter, to let
+// the AsanPoisoningHelper instances be compiled to nothing
+template<>
+class AsanPoisoningHelper<void> {
+ public:
+  AsanPoisoningHelper() = delete;
+  AsanPoisoningHelper(void* addr) { }
+  static void poison_memory(void* addr) { }
+  static void unpoison_memory(void* addr) { }
+  ~AsanPoisoningHelper() { }
+};
+
+#ifdef INCLUDE_ASAN
+  using CanaryType = uint16_t;
+  using SizeType = size_t;
+  NOT_LP64(using AltCanaryType = uint32_t;)
+#else
+  using CanaryType = void;
+  using SizeType = void;
+  NOT_LP64(using AltCanaryType = void;)
+#endif
+
