+  MallocHeader* mh = (MallocHeader*)(p - sizeof(MallocHeader));
+  CanaryType* footer_ptr = (CanaryType*)(mh->footer_address());
+  CanaryType read_footer = *footer_ptr;
+}
+
+static void test_write_size() {
