-    private boolean checkCovered(Type seltype, Iterable<PatternDescription> patterns) {
-        for (Type seltypeComponent : components(seltype)) {
-            for (PatternDescription pd : patterns) {
-                if(isBpCovered(seltypeComponent, pd)) {
-                    return true;
-                }
-            }
-        }
-        return false;
-    }
