+package sun.net.httpserver;
+
+import java.io.IOException;
+import java.io.InputStream;
+
+/**
+ * A class which allows the caller to read the underlying stream for an upgrade request.
+ * close() does not close the underlying stream
+ */
+class UpgradeInputStream extends LeftOverInputStream {
+
+    UpgradeInputStream(ExchangeImpl t, InputStream src) {
+        super(t, src);
+    }
+
+    @Override
+    protected int readImpl(byte[] b, int off, int len) throws IOException {
+        if (!t.upgraded) {
+            return -1;
+        }
+        return in.read(b, off, len);
+    }
+
+    @Override
+    public int available() throws IOException {
+        if (!t.upgraded) {
+            return 0;
+        }
+        return in.available();
+    }
+
+    @Override
+    public void reset() throws IOException {
+        throw new IOException("mark/reset not supported");
+    }
+
+    @Override
+    public void close() throws IOException {
+        closed = true;
+        t.getServerImpl().requestCompleted(t.getConnection());
+    }
+
+    @Override
+    public byte[] readAllBytes() throws IOException {
+        if (!t.upgraded) {
+          return new byte[0];
+        }
+        throw new IOException("readAllBytes not supported for upgraded requests");
+    }
+}
