+                while ((line = err.readLine()) != null) {
+                    Log.error(line);
+                }
+            } catch (IOException ioe) {
+                  Log.verbose(ioe);
+            }
+        });
+        t.setDaemon(true);
+        t.start();
+
+        int ret = p.waitFor();
+        Log.verbose(pb.command(), list, ret, IOUtils.getPID(p));
+
+        result.clear();
+        result.addAll(list);
+
+        return ret;
