@@ -534,0 +537,10 @@
+            if (menuItem.getHorizontalTextPosition() != SwingConstants.CENTER) {
+                int d = textR.x - iconR.x;
+                if (d > 0) {
+                    textR.x = iconR.x;
+                    iconR.x = iconR.x + d + textR.width - iconR.width;
+                } else {
+                    iconR.x = textR.x;
+                    textR.x = iconR.x - d + iconR.width - textR.width;
+                }
+            }
