-            sessionK[1] = genInvRoundKeys(sessionK[0], rounds);
@@ -937,1 +938,9 @@
-        K = sessionK[decrypt];
+
+        if (decrypting) {
+            if (sessionKd == null) {
+                sessionKd = genInvRoundKeys(sessionKe, rounds);
+            }
+            K = sessionKd;
+        } else {
+            K = sessionKe;
+        }
