files.patch:
@@ -1702,0 +1702,2 @@
+  // If any floating point comparison instruction is used, unordered case never triggers jump
+  // For above equal condition, CF=0 is false when at least one input is NaN
@@ -1703,10 +1705,3 @@
-  if (VM_Version::supports_avx10_2()) {
-    __ setcc(Assembler::above, dst);
-    __ jcc(Assembler::aboveEqual, done);
-    __ movl(dst, -1);
-  } else {
-    __ movl(dst, -1);
-    __ jcc(Assembler::parity, done);
-    __ jcc(Assembler::below, done);
-    __ setcc(Assembler::notEqual, dst);
-  }
+  __ setcc(Assembler::above, dst);
+  __ jcc(Assembler::aboveEqual, done);
+  __ movl(dst, -1);
