+    log_info(os, cpu)("Disabled RV64 feature \"%s\" (%ld)", pretty(), value());
+  } else {
+    log_info(os, cpu)("Disabled RV64 feature \"%s\" (%ld) (%s)", pretty(), value(), reason);
