files.patch:
@@ -629,0 +629,17 @@
+  if (!is_in_reserved(p)) {
+    // If it's not even in reserved.
+    return false;
+  }
+
+#ifdef ASSERT
+  {
+    // Java threads in native-state don't participate in safepoint, where heap
+    // resizing can occur.
+    // is_in can fail if resizing is in process, so disallow callers in native-state.
+    if (Thread::current()->is_Java_thread()) {
+      JavaThread* thread = JavaThread::current();
+      assert(thread->thread_state() != _thread_in_native, "precondition");
+    }
+  }
+#endif
+
