@@ -3615,0 +3615,12 @@
+    @Override
+    @ForceInline
+    final
+    DoubleVector maybeSwapOnConverted(ByteOrder bo, AbstractSpecies<?> srcSpecies) {
+        int subLanesPerSrc = subLanesPerSrcIfNeeded(bo, srcSpecies);
+        if (subLanesPerSrc < 0) {
+            return this;
+         }
+        VectorShuffle<Double> shuffle = normalizeSubLanesForSpecies(this.vspecies(), subLanesPerSrc);
+        return (DoubleVector) this.rearrange(shuffle);
+    }
