+void LIR_Assembler::increment_profile_ctr(LIR_Opr step, LIR_Opr counter_addr, LIR_Opr dest, LIR_Opr temp_op,
+                                          LIR_Opr freq_op,
+                                          CodeStub* overflow_stub) {
+#ifndef PRODUCT
+  if (CommentedAssembly) {
+    __ block_comment("increment_event_counter {");
+  }
+#endif
+
+  int profile_capture_ratio = ProfileCaptureRatio;
+  int ratio_shift = exact_log2(profile_capture_ratio);
+  unsigned long threshold = (UCONST64(1) << 32) >> ratio_shift;
+
+  assert(threshold > 0, "must be");
+
+  ProfileStub *counter_stub
+    = profile_capture_ratio > 1 ? new ProfileStub() : nullptr;
+
+  Register temp = temp_op->is_register() ? temp_op->as_register() : noreg;
+  Address raw_dest_adr = as_Address(counter_addr->as_address_ptr());
+
+  auto lambda = [counter_stub, overflow_stub, freq_op, ratio_shift, step,
+                 temp, dest, raw_dest_adr] (LIR_Assembler* ce, LIR_Op* op) {
+
+#undef __
+#define __ masm->
+
+    auto masm = ce->masm();
+
+    if (counter_stub != nullptr)  __ bind(*counter_stub->entry());
+
+    if (step->is_register()) {
+      Address dest_adr = __ legitimize_address(raw_dest_adr, sizeof (jint), rscratch2);
+      Register inc = step->as_register();
+      __ ldrw(temp, dest_adr);
+      if (ProfileCaptureRatio > 1) {
+        __ lsl(inc, inc, ratio_shift);
+      }
+      __ addw(temp, temp, inc);
+      __ strw(temp, dest_adr);
+      if (dest->is_register())  __ mov(dest->as_register(), temp);
+      if (ProfileCaptureRatio > 1) {
+        __ lsr(inc, inc, ratio_shift);
+      }
+      if (dest->is_register())  __ mov(dest->as_register(), temp);
+    } else {
+      jint inc = step->as_constant_ptr()->as_jint_bits();
+      switch (dest->type()) {
+        case T_INT: {
+          Address dest_adr = __ legitimize_address(raw_dest_adr, sizeof (jint), rscratch2);
+          inc *= ProfileCaptureRatio;
+          __ incrementw(dest_adr, inc, temp);
+          if (dest->is_register())  __ movw(dest->as_register(), temp);
+
+          break;
+        }
+        case T_LONG: {
+          Address dest_adr = __ legitimize_address(raw_dest_adr, sizeof (jlong), rscratch2);
+          inc *= ProfileCaptureRatio;
+          __ increment(dest_adr, inc, temp);
+          if (dest->is_register())  __ mov(dest->as_register_lo(), temp);
+
+          break;
+        }
+        default:
+          ShouldNotReachHere();
+      }
+
+      if (step->is_valid() && overflow_stub) {
+        if (!freq_op->is_valid()) {
+          if (!step->is_constant()) {
+            __ cbz(step->as_register(), *overflow_stub->entry());
+          } else {
+            __ b(*overflow_stub->entry());
+            return;
+          }
+        } else {
+          Register result =
+            dest->type() == T_INT ? dest->as_register() :
+            dest->type() == T_LONG ? dest->as_register_lo() :
+            noreg;
+          if (!step->is_constant()) {
+            // If step is 0, make sure the stub check below always fails
+            __ cmp(step->as_register(), (u1)0);
+            __ mov(temp, InvocationCounter::count_increment * ProfileCaptureRatio);
+            __ csel(result, result, temp, __ NE);
+          }
+          juint mask = freq_op->as_jint();
+          __ andw(rscratch1, result,  mask);
+          __ cbzw(rscratch1, *overflow_stub->entry());
+        }
+      }
+    }
+
+    if (counter_stub != nullptr) {
+      __ b(*counter_stub->continuation());
+    }
+
+#undef __
+#define __ _masm->
+  };
+
+  if (counter_stub != nullptr) {
+    __ ubfx(rscratch1, r_profile_rng, 32 - ratio_shift, ratio_shift);
+    __ cbz(rscratch1, *counter_stub->entry());
+    __ bind(*counter_stub->continuation());
+    __ step_random(r_profile_rng, temp);
+
+    counter_stub->set_action(lambda, nullptr);
+    counter_stub->set_name("IncrementEventCounter");
+    append_code_stub(counter_stub);
+  } else {
+    lambda(this, nullptr);
+  }
+
+  if (overflow_stub != nullptr) {
+    __ bind(*overflow_stub->continuation());
+  }
+
+#ifndef PRODUCT
+  if (CommentedAssembly) {
+    __ block_comment("} increment_event_counter");
+  }
+#endif
+}
