+        @ForceInline
+        MethodHandle adaptedMethodHandle(VarHandle vh) {
+            if (MethodHandleImpl.isCompileConstant(vh)) {
+                var cache = lastAdaption;
+                if (cache != null) {
+                    return cache;
+                }
+            }
