@@ -226,0 +226,24 @@
+
+// ------------------------------------------------------------------
+// ciObject::identity_hash
+//
+// The identity hash code of an object or an empty constant if a hash is not computed yet.
+// Observed value is cached, so it doesn't change during compilation.
+ciConstant ciObject::identity_hash() {
+  if (!is_null_object()) {
+    // Handle identity hash as if it were a field.
+    ciConstant value = check_constant_value_cache(IDENTITY_HASH_OFFSET, T_INT);
+    if (!value.is_valid()) {
+      VM_ENTRY_MARK;
+      jint identity_hash = checked_cast<jint>(get_oop()->fast_identity_hash_or_no_hash());
+      value = ciConstant(T_INT, identity_hash);
+      // Cache observed state so it doesn't change during compilation.
+      add_to_constant_value_cache(IDENTITY_HASH_OFFSET, value);
+    }
+    if (value.as_int() != markWord::no_hash) {
+      return value;
+    }
+  }
+  return ciConstant(); // no hash computed
+}
+

@@ -440,0 +440,15 @@
+// Returns the identity hash if it is present (doesn't compute it).
+intptr_t oopDesc::fast_identity_hash_or_no_hash() {
+  // Note: The mark must be read into local variable to avoid concurrent updates.
+  markWord mrk = mark_acquire();
+  assert(!mrk.is_marked(), "should never be marked");
+  // Fast case: if the object is unlocked and the hash value is set, no locking is needed to read it.
+  // If UseObjectMonitorTable is set, the hash can simply be installed in the
+  // object header, since the monitor isn't in the object header.
+  if (UseObjectMonitorTable || !mrk.has_monitor()) {
+    return mrk.hash();
+  } else {
+    return markWord::no_hash;
+  }
+}
+

@@ -4802,0 +4802,12 @@
+  const TypeInstPtr* t = _gvn.type(obj)->isa_instptr();
+  if (t != nullptr && t->const_oop() != nullptr) {
+    assert(!is_virtual, "no devirtualization for constant receiver?");
+    ciConstant identity_hash = t->const_oop()->identity_hash();
+    if (identity_hash.is_valid()) {
+      result_reg->init_req(_fast_path, control());
+      result_val->init_req(_fast_path, _gvn.intcon(identity_hash.as_int()));
+      set_result(result_reg, result_val);
+      return true;
+    }
+  }
