+/**
+ * @test
+ * @summary Verify that no deadlock will happen during JVM shutdown hooks
+ * @requires vm.hasJFR
+ * @library /test/lib
+ * @run main/othervm/timeout=10  jdk.jfr.api.recording.deadlock.TestShutdownDeadLock
+ */
+package jdk.jfr.api.recording.deadlock;
+
+import java.time.Duration;
+import java.nio.file.Files;
+import java.nio.file.Path;
+
+import jdk.jfr.Recording;
+
+import jdk.test.lib.Asserts;
+
+public class TestShutdownDeadLock {
+
+    public static void main(String[] args) {
+        // Initialize JFR subsystem
+        Recording r = new Recording();
+        r.start();
+        r.stop();
+
+        Runtime.getRuntime().addShutdownHook(new Thread(() -> {
+            Exception thrownException = null;
+            Path temp;
+
+            try {
+                Thread.sleep(100);
+
+                Recording recording = new Recording();
+                recording.scheduleStart(Duration.ofSeconds(100));
+                recording.start();
+                temp = Files.createTempFile("", ".tmp");
+                temp.toFile().deleteOnExit();
+                recording.dump(temp);
+                recording.copy(false);
+                recording.stop();
+            } catch (Exception e) {
+                thrownException = e;
+                e.printStackTrace(System.err);
+            }
+            Asserts.assertNull(thrownException);
+        }));
+
+        System.out.println("Exiting");
+    }
+}
