+package compiler.escapeAnalysis;
+
+import compiler.lib.ir_framework.*;
+
+import java.lang.invoke.VarHandle;
+import java.util.function.Supplier;
+
+/**
+ * @test
+ * @bug 8373495
+ * @summary Test that loads from a newly allocated object are aggressively folded if the object has not escaped
+ * @library /test/lib /
+ * @run driver ${test.main.class}
+ */
+public class TestLoadFolding {
+    public static class Point {
+        int x;
+        int y;
+
+        Point() {
+            x = 1;
+            y = 2;
+        }
+
+        static final Point DEFAULT = new Point();
+    }
+
+    static Point staticField;
+
+    public static void main(String[] args) {
+        var framework = new TestFramework();
+        framework.setDefaultWarmup(1);
+        framework.start();
+    }
+
+    @DontInline
+    static void escape(Object o) {}
+
+    @Test
+    @IR(failOn = IRNode.LOAD_I, counts = {IRNode.ALLOC, "1"})
+    public Point test11() {
+        // p only escapes at return
+        Point p = new Point();
+        escape(null);
+        p.x += p.y;
+        return p;
+    }
+
+    @Test
+    @IR(failOn = IRNode.LOAD_I, counts = {IRNode.ALLOC, "1"})
+    public Point test12(boolean b) {
+        // p escapes in another branch
+        Point p = new Point();
+        if (b) {
+            escape(p);
+        } else {
+            escape(null);
+            p.x += p.y;
+        }
+        return p;
+    }
+
+    @Test
+    @IR(failOn = IRNode.LOAD_I, counts = {IRNode.ALLOC, "1"})
+    public Point test13(boolean b) {
+        // A Phi of p1 and Point.DEFAULT, but a store to Phi is after all the loads from p1
+        Point p1 = new Point();
+        Point p = b ? p1 : Point.DEFAULT;
+        escape(null);
+        p.x = p1.x + p1.y;
+        return p;
+    }
+
+    @Test
+    @IR(failOn = IRNode.LOAD_I, counts = {IRNode.ALLOC, "1"})
+    public int test14() {
+        // Even if p escapes before the loads, if it is legal to execute the loads before the
+        // store, then we can fold the loads
+        Point p = new Point();
+        escape(null);
+        staticField = p;
+        return p.x + p.y;
+    }
+
+    @Test
+    @IR(counts = {IRNode.ALLOC, "1"})
+    public Point test15(int begin, int end) {
+        // Fold the load that is a part of a cycle
+        Point p = new Point();
+        for (int i = begin; i < end; i *= 2) {
+            p.x++;
+            escape(null); // Force a memory Phi
+        }
+        p.x += p.y;
+        return p;
+    }
+
+    @Test
+    @IR(counts = {IRNode.ALLOC, "1"})
+    public Point test16(int begin, int end, boolean b) {
+        // A cycle and a Phi, this time the store is at a different field
+        Point p1 = new Point();
+        // This store is not on a Phi involving p1, so it does not interfere
+        Point.DEFAULT.y = 3;
+        Point p = p1;
+        for (int i = begin; i < end; i += 2) {
+            if (b) {
+                p = p1;
+            } else {
+                p = Point.DEFAULT;
+            }
+            b = !b;
+
+            p.x = p1.y + 3;
+            escape(null); // Force a memory Phi
+        }
+        p1.x = p1.y;
+        return p;
+    }
+
+    @Test
+    @IR(counts = {IRNode.LOAD_I, "1", IRNode.ALLOC_ARRAY, "1"})
+    public int test17(int idx) {
+        // Array
+        int[] a = new int[2];
+        a[0] = 1;
+        a[1] = 2;
+        int res = a[idx & 1];
+        escape(null);
+        res += a[0] + a[1];
+        escape(a);
+        return res;
+    }
+
+    @Test
+    @IR(failOn = IRNode.LOAD_I, counts = {IRNode.ALLOC_ARRAY, "1"})
+    public int test18(int idx) {
+        // Array, even if we will give up if we encounter a[idx & 1] = 3, we meet a[0] = 4 first,
+        // so the load int res = a[0] can still be folded
+        int[] a = new int[2];
+        a[0] = 1;
+        a[1] = 2;
+        escape(null);
+        a[idx & 1] = 3;
+        a[0] = 4;
+        escape(null);
+        int res = a[0];
+        escape(a);
+        return res;
+    }
+
+    static class SupplierHolder {
+        Supplier<String> f;
+
+        static final Supplier<String> DEFAULT_VALUE = () -> "test";
+    }
+
+    @Test
+    @IR(failOn = {IRNode.DYNAMIC_CALL_OF_METHOD, "get", IRNode.LOAD_OF_FIELD, "f", IRNode.CLASS_CHECK_TRAP}, counts = {IRNode.ALLOC, "1"})
+    public String test19() {
+        // Folding of the load o.f allows o.f.get to get devirtualized
+        SupplierHolder o = new SupplierHolder();
+        o.f = SupplierHolder.DEFAULT_VALUE;
+        escape(null);
+        String res = o.f.get();
+        escape(o);
+        return res;
+    }
+
+    @Run(test = {"test11", "test12", "test13", "test14", "test15", "test16", "test17", "test18", "test19"})
+    public void runPositiveTests() {
+        test11();
+        test12(false);
+        test12(true);
+        test13(false);
+        test13(true);
+        test14();
+        test15(1, 16);
+        test16(1, 16, false);
+        test16(1, 16, true);
+        test17(0);
+        test18(0);
+        test19();
+    }
+
+    @Test
+    @IR(counts = {IRNode.LOAD_I, "2", IRNode.ALLOC, "1"})
+    public int test01() {
+        Point p = new Point();
+        staticField = p;
+        // Actually, the only fence that requires the following loads to be executed after the
+        // store is a fullFence
+        VarHandle.fullFence();
+        return p.x + p.y;
+    }
+
+    @Test
+    @IR(counts = {IRNode.LOAD_I, "1", IRNode.ALLOC, "1"})
+    public int test02(boolean b) {
+        Point p = new Point();
+        if (b) {
+            escape(p);
+            // p escaped, so the load must not be removed
+            return p.x;
+        } else {
+            escape(null);
+            return 0;
+        }
+    }
+
+    @Test
+    @IR(counts = {IRNode.LOAD_I, "1", IRNode.ALLOC, "1"})
+    public int test03(boolean b) {
+        Point p = new Point();
+        if (b) {
+            escape(p);
+        }
+        // p escaped, so the load must not be removed
+        return p.x;
+    }
+
+    @Test
+    @IR(counts = {IRNode.LOAD_I, "> 0", IRNode.ALLOC, "1"})
+    public Point test04(int begin, int end) {
+        Point p = new Point();
+        for (int i = begin; i < end; i *= 2) {
+            // p escaped here because this is a loop
+            p.x++;
+            escape(p);
+        }
+        return p;
+    }
+
+    @Test
+    @IR(counts = {IRNode.LOAD_I, "2", IRNode.ALLOC_ARRAY, "1"})
+    public int test05(int idx) {
+        int[] a = new int[2];
+        a[0] = 1;
+        a[1] = 2;
+        escape(null);
+        a[idx & 1] = 3;
+        // Cannot fold the loads because we do not know which element is written to by
+        // a[idx & 1] = 3
+        return a[0] + a[1];
+    }
+
+    @Run(test = {"test01", "test02", "test03", "test04", "test05"})
+    public void runNegativeTests() {
+        test01();
+        test02(false);
+        test02(true);
+        test03(false);
+        test03(true);
+        test04(1, 16);
+        test05(0);
+    }
+}
