+static inline cpu_time_t detailed_thread_cpu_time_or_zero(Thread* thread) {
+  cpu_time_t cpu_time = os::detailed_thread_cpu_time(thread);
+  if (cpu_time.system == -1 || cpu_time.user == -1) {
+    CPUTimeUsage::Error::mark_error();
+    return { 0, 0 };
+  }
+  return cpu_time;
+}
