-
-    a->remove_range(0, 1);
-    ASSERT_EQ(a->length(), 1);
-    ASSERT_EQ(a->at(0), 8);
-
-    a->remove_range(0, 1);
-    ASSERT_EQ(a->length(), 0);
-
-    // Remove elements from empty list with empty range, should be accepted
-    a->remove_range(0, 0);
-    ASSERT_EQ(a->length(), 0);
