+                ResourceCleanup prev = null;
+                while (true) {
+                    prev = (ResourceCleanup) FST.getAcquire(this);
+                    // no need to check for DUMMY, since only one thread can get here!
+                    if (FST.weakCompareAndSetRelease(this, prev, ResourceCleanup.CLOSED_LIST)) {
+                        break;
+                    }
