+G1PostEvacuateCollectionSetCleanupTask2::RedirtyLoggedCardsTask::RedirtyLoggedCardsTask(G1RedirtyCardsQueueSet* rdcqs) :
+  G1AbstractSubTask(G1GCPhaseTimes::RedirtyCards),
+  _rdcqs(rdcqs),
+  _nodes(rdcqs->all_completed_buffers()) { }
