+template <class Elem>
+G1CardSetAllocator<Elem>::G1CardSetAllocator(const char* name,
+                                             const G1CardSetAllocOptions& buffer_options,
+                                             G1CardSetBufferList* free_buffer_list) :
+  _alloc_options(buffer_options),
