+  _mem_size(0),
+  _free_buffer_list(free_buffer_list),
+  _transfer_lock(false),
+  _free_nodes_list(),
+  _pending_nodes_list(),
+  _num_pending_nodes(0),
+  _num_free_nodes(0),
+  _num_allocated_nodes(0),
+  _num_available_nodes(0)
