+    G1CountCardsClosure count_cards;
+    card_set.iterate_cards(count_cards);
+    ASSERT_TRUE(count_cards._num_cards == ARRAY_SIZE(cards1));
+
+    check_iteration(&card_set, card_set.occupied());
