+  for (size_t i = 0; i < nelements; ++i) {
+    ASSERT_FALSE(queue.empty());
+    ASSERT_EQ(nelements - i, queue.length());
+    Element* e = queue.pop();
+    ASSERT_TRUE(e != NULL);
+    ASSERT_EQ(&elements[i], e);
+    ASSERT_EQ(i, e->id());
+  }
+  ASSERT_TRUE(queue.empty());
+  ASSERT_EQ(0u, queue.length());
+  ASSERT_TRUE(queue.pop() == NULL);
+}
