-        Server(HttpCallback cb, ServerSocketChannel schan, int maxconn) {
-            this.schan = schan;
-            this.maxconn = maxconn;
-            this.cb = cb;
-            nconn = 0;
-            consumeBuffer = ByteBuffer.allocate(512);
-            clist = new ClosedChannelList();
-            try {
-                selector = Selector.open();
-                schan.configureBlocking(false);
-                listenerKey = schan.register(selector, SelectionKey.OP_ACCEPT);
-            } catch (IOException e) {
-                System.err.println("Server could not start: " + e);
-            }
-        }
-
-        /* Stop the thread as soon as possible */
-        public synchronized void terminate() {
-            shutdown = true;
-        }
-
-        public void run()  {
-            try {
-                while (true) {
-                    selector.select(1000);
-                    Set selected = selector.selectedKeys();
-                    Iterator iter = selected.iterator();
-                    while (iter.hasNext()) {
-                        key = (SelectionKey)iter.next();
-                        if (key.equals (listenerKey)) {
-                            SocketChannel sock = schan.accept();
-                            if (sock == null) {
-                                /* false notification */
-                                iter.remove();
-                                continue;
-                            }
-                            sock.configureBlocking(true);
-                            SSLEngine sslEng = sslCtx.createSSLEngine();
-                            sslEng.setUseClientMode(false);
-                            new ServerWorker(cb, sock, sslEng).start();
-                            nconn ++;
-                            if (nconn == maxconn) {
-                                /* deregister */
-                                listenerKey.cancel();
-                                listenerKey = null;
-                            }
-                        } else {
-                            if (key.isReadable()) {
-                                boolean closed = false;
-                                SocketChannel chan = (SocketChannel)key.channel();
-                                if (key.attachment() != null) {
-                                    closed = consume(chan);
-                                }
-
-                                if (closed) {
-                                    chan.close();
-                                    key.cancel();
-                                    if (nconn == maxconn) {
-                                        listenerKey = schan.register(selector, SelectionKey.OP_ACCEPT);
-                                    }
-                                    nconn --;
-                                }
-                            }
-                        }
-                        iter.remove();
-                    }
-                    clist.check();
-
-                    synchronized (this) {
-                        if (shutdown) {
-                            clist.terminate();
-                            return;
-                        }
-                    }
-                }
-            } catch (IOException e) {
-                System.out.println("Server exception: " + e);
-                // TODO finish
-            }
-        }
