-        /* read all the data off the channel without looking at it
-         * return true if connection closed
-         */
-        boolean consume(SocketChannel chan) {
-            try {
-                consumeBuffer.clear();
-                int c = chan.read(consumeBuffer);
-                if (c == -1)
-                    return true;
-            } catch (IOException e) {
-                return true;
-            }
-            return false;
-        }
-    }
