-        byte[] readNormalData(InputStream is, int len) throws IOException {
-            byte[] buf  = new byte[len];
-            int c, off=0, remain=len;
-            while (remain > 0 && ((c=is.read (buf, off, remain))>0)) {
-                remain -= c;
-                off += c;
-            }
-            return buf;
-        }
