-        private String readLine(InputStream is) throws IOException {
-            boolean done=false, readCR=false;
-            byte[] b = new byte[512];
-            int c, l = 0;
-
-            while (!done) {
-                c = is.read();
-                if (c == '\n' && readCR) {
-                    done = true;
-                } else {
-                    if (c == '\r' && !readCR) {
-                        readCR = true;
-                    } else {
-                        b[l++] = (byte)c;
-                    }
-                }
-            }
-            return new String(b);
-        }
