+        BufferedImage big = new BufferedImage(IMG_WIDTH*2, IMG_HEIGHT*2, BufferedImage.TYPE_INT_RGB);
+        drawEmoji(small, EMOJI_X, EMOJI_Y, FONT_SIZE);
