+// java.awt.Color usecase
+class Color {
+    int value;
+    public Color(int r, int g, int b, int a) {
+        // 1. lsl   w11, w5, #24
+        // 2. bfi   x11, x2, #16, #8
+        // 3. bfi   x11, x3, #8, #8
+        // 4. bfxil x11, x4, #0, #8
+        value = ((a & 0xFF) << 24) |
+                ((r & 0xFF) << 16) |
+                ((g & 0xFF) << 8)  |
+                ((b & 0xFF) << 0);
+    }
+    static int instrCount = 4;
+    static boolean useBFI = true;
+}
+
+// reverse byte order
+class ColorReverse {
+    int value;
+    public ColorReverse(int r, int g, int b, int a, int dummy) {
+        // 1. and  w11, w4, #0xff
+        // 2. bfi  x11, x3, #8, #8
+        // 3. bfi  x11, x2, #16, #8
+        // 4. orr  w10, w11, w5, lsl #24
+        value = ((a & 0xFF) << 0)   |
+                ((r & 0xFF) << 8)   |
+                ((g & 0xFF) << 16)  |
+                ((b & 0xFF) << 24);
+    }
+    static int instrCount = 4;
+    static boolean useBFI = true;
+}
+
+// the same but PLUS instead of OR
+class ColorPlus {
+    int value;
+    public ColorPlus(int r, int g, int b, int a) {
+        // 1. lsl   w11, w5, #24
+        // 2. bfi   x11, x2, #16, #8
+        // 3. bfi   x11, x3, #8, #8
+        // 4. bfxil x11, x4, #0, #8
+        value = ((a & 0xFF) << 24) +
+                ((r & 0xFF) << 16) +
+                ((g & 0xFF) << 8)  +
+                ((b & 0xFF) << 0);
+    }
+    static int instrCount = 4;
+    static boolean useBFI = true;
+}
+
+// same for long type
+class ColorLongPlus {
+    long value;
+    public ColorLongPlus(long r, long g, long b, long a) {
+        // 1. lsl   w11, w5, #24
+        // 2. bfi   x11, x2, #16, #8
+        // 3. bfi   x11, x3, #8, #8
+        // 4. bfxil x11, x4, #0, #8
+        value = ((a & 0xFF) << 24) +
+                ((r & 0xFF) << 16) +
+                ((g & 0xFF) << 8)  +
+                ((b & 0xFF) << 0);
+    }
+    static int instrCount = 4;
+    static boolean useBFI = true;
+}
+
+class BigDecimal {
+    static final long LONG_MASK = 0xffffffffL;
+    static long mulsub(long u1, long u0, final long v1, final long v0, long q0) {
+        long tmp = u0 - q0 * v0;
+        long tmp1 = u1 + (tmp>>>32) - q0 * v1;
+        // 1. lsl R0, R11, #32
+        // 2. bfi R0, R10, ##0, ##32
+        return (tmp1 << 32) | (tmp & LONG_MASK);
+    }
+    static int instrCount = 2;
+    static boolean useBFI = true;
+}
