+
+template<typename CB>
+void launch_v(CB cb) {
+    std::thread thrd(cb);
+    thrd.join();
+}
+
+template<typename O, typename CB>
+void start(O& out, CB cb) {
+    out = cb();
+}
+
+template<typename O, typename CB>
+O launch(CB cb) {
+    O result;
+    std::thread thrd(&start<O, CB>, std::ref(result), cb);
+    thrd.join();
+    return result;
+}
