+static bool initialize_libperfstat() {
+  static bool is_libperfstat_loaded = false;
+
+  if (!is_libperfstat_loaded) {
+    is_libperfstat_loaded = libperfstat::init();
+  }
+
+  // If library is still not initialized, assume error.
