+    static constexpr uint8_t DW_LNS_set_basic_block = 7;
+    static constexpr uint8_t DW_LNS_const_add_pc = 8;
+    static constexpr uint8_t DW_LNS_fixed_advance_pc = 9;
+    static constexpr uint8_t DW_LNS_set_prologue_end = 10;
