+            head->predecessors()->remove(lp2->tail());
+            clone->predecessors()->append(lp2->tail());
+          }
+        }
+        flow_block(lp2->tail(), temp_vector, temp_set);
+        if (total_count == 0 || count > (total_count * .9)) {
+          done = true;
+        }
+      }
+    }
+  }
